Recipe.create!([
  {name: "Jules", description: "Something Here", ingredients: "Another Thing here", directions: "sdlasdf asdhfas dfahsdfasd", ratings: nil, author_id: 5, dessert_type_id: nil, image_file_name: "Desserts_Brownie_Bite.jpg", image_content_type: "image/jpeg", image_file_size: 37615, image_updated_at: "2015-12-11 04:00:37"},
  {name: "Sugar Coated Bitter Pill", description: "Prepare this meal at your own risk", ingredients: "Any bitter ingredient of your choice is cool", directions: "Lets do it", ratings: nil, author_id: 5, dessert_type_id: nil, image_file_name: "55959.jpg", image_content_type: "image/jpeg", image_file_size: 39038, image_updated_at: "2015-12-11 04:00:39"},
  {name: "Cranberry Upside-Down Sour Cream Cake", description: "\"I received this recipe a couple of years ago from a friend and get rave reviews from everyone each time I prepare it. It is not only delicious, but very pretty for a holiday table. Great for Thanksgiving or Christmas.\"", ingredients: "1/2 cup butter\r\n2 cups white sugar\r\n2 tablespoons water\r\n1 teaspoon ground cinnamon\r\n1 (12 ounce) bag fresh or frozen cranberries\r\n1 1/2 cups cake flour\r\n1/2 teaspoon baking soda", directions: "This is the beginning of the end\r\n\r\nPreheat the oven to 350 degrees F (175 degrees C). Generously grease a 9 inch springform pan. Wrap aluminum foil around the outside of the bottom to prevent leaking.\r\n\r\nMelt the butter in a saucepan over medium heat. Stir in 1 1/2 cups of white sugar, water and cinnamon until sugar has dissolved. Bring to a boil and then add the cranberries. Stir to coat with the sauce, then pour into the prepared pan.\r\n\r\nSift together the flour, baking soda and salt; set aside. In a medium bowl, beat the remaining 6 tablespoons of butter with 1/2 cup white sugar and brown sugar until light and fluffy.\r\n\r\nMix in the eggs one at a time, beating well after each addition. Stir in vanilla and sour cream. Mix in the dry ingredients. Pour the batter over the cranberries in the pan.\r\n\r\nBake for about 50 minutes in the preheated oven, or until a knife inserted into the center comes out clean. Cool on a rack for 10 minutes, then run a knife around the outer edge. Invert onto a serving plate and remove the springform pan.", ratings: nil, author_id: 2, dessert_type_id: 5, image_file_name: "cranberry_cake_recipe.jpg", image_content_type: "image/jpeg", image_file_size: 114707, image_updated_at: "2015-12-11 04:00:39"},
  {name: "Fish Bowl", description: "ashldfasd fasdhfasd f", ingredients: "alkshdfas dfas dfa sdfh", directions: "asd falhsdf asdhfas df", ratings: nil, author_id: 3, dessert_type_id: 8, image_file_name: "60619.jpg", image_content_type: "image/jpeg", image_file_size: 38557, image_updated_at: "2015-12-11 04:00:39"},
  {name: "early morning starters", description: "Single layer cake", ingredients: "flour, eggs, vanilla ice cream, butter, raisins, cream", directions: "mix 2 eggs into a 100gm flour. Add butter and cream", ratings: nil, author_id: 2, dessert_type_id: 5, image_file_name: "dessert.jpg", image_content_type: "image/jpeg", image_file_size: 31582, image_updated_at: "2015-12-11 04:00:40"},
